function escondeTodasAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("movie__list--is-active")}function removeBotaoAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("movie__tabs__button--is-active")}function ocultaElementosDoHeader(){document.querySelector("header").classList.add("header--is-hidden")}function exibeElementosDoHeader(){document.querySelector("header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElementosDoHeader:exibeElementosDoHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeTodasAbas(),t.classList.add("movie__list--is-active"),removeBotaoAtivo(),e.target.classList.add("movie__tabs__button--is-active")})});